import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';

function Search() {
    const [showSearch, setShowSearch] = useState(false)

    let searchBox, searchBoxMask

    if(showSearch) {
        searchBox = 
            <div className = "fixed bg-white top-1/2 left-1/2 w-1/3 z-50 shadow">
                This is the searchBox
            </div>

        searchBoxMask =
            <div className = "bg-red fixed top-0 left-0 w-full h-full z-50"></div>
    }

    return (
        <div>
            <span className="text-center font-bold p-3">
                <FontAwesomeIcon
                    icon = {faSearch}
                    onClick = {() => setShowSearch(!showSearch)}
                />
            </span>

        {searchBoxMask}
        {searchBox}

        </div>
    );
}

export default Search
