import React, {useState, useEffect, Fragment} from 'react';
import axios from 'axios';
import Loader from '../Components/Loader';
import { Link } from 'react-router-dom';

function Home() {
    const url = 'https://iot.fvh.fi/opendata/uiras/uiras2_v1.json'
    const [beaches, setBeaches] = useState({loading:false, dt:null})
    let content = null

    useEffect(() => {
        setBeaches({
            loading: true, 
            dt: null
        })

        axios.get(url)
            .then(response => {
                setBeaches({
                    loading: false,
                    dt: response.data.sensors,
                })
        })
            .catch((err) => {
                console.log(err)
            })
    }, [])

    if(beaches.loading) {
        content = <Loader />
    }

    if(beaches.dt) {
        content = Object.keys(beaches.dt).map((beach, key) => {
            return (
                <h1 className = "p-5" /*beach = {beaches.dt[beach]}*/ key = {key}> 
                    <Link to = {`/${Object.keys(beaches.dt[beach].meta)}`}>
                        {beaches.dt[beach].meta.name}: {beaches.dt[beach].data.slice(-1)[0].temp_water}Â°C
                    </Link>
                </h1>
            )
        })
    }

    return (
        <div> {content} </div>
    )
}

export default Home
