import React, {useState, useEffect} from 'react';
import axios from 'axios';
import Loader from '../Components/Loader'
import { useParams } from 'react-router-dom';

function SinglePage(props) {
    const url = "https://iot.fvh.fi/opendata/uiras/uiras2_v1.json"
    const [beach, setBeach] = useState({loading:false, dt:null})
    let content = null

    useEffect(() => {
        setBeach({
            loading: true, 
            dt: null
        })

        axios.get(url)
            .then(response => {
                setBeach({
                    loading: false,
                    dt: response.data.sensors
                })
        })
            .catch((err) => {
                console.log(err)
            })
    }, [])

    if(beach.loading) {
        content = <Loader />
    }
    //get temp: {beach.dt[beach].data.slice(-1)[0].temp_water}
    /*
        content = Object.keys(beach).map((item, key) => {

            return (
                <h1 className = "p-5" key = {key}> 
                    This is the single page for {props.route.params}
                </h1>
            )
        }) 
    */

    if(beach) {
        return (
            <div>
                {beach.sensors[0].meta.name}
            </div>
        )
    }

    return (
        <div></div>
    )
}

export default SinglePage
