import React, {useState, useEffect} from 'react';
import axios from 'axios';
import Loader from '../Components/Loader'
import { useParams } from 'react-router-dom';

function SinglePage(props) {
    const {name} = useParams();
    const url = "https://iot.fvh.fi/opendata/uiras/uiras2_v1.json"
    const [beach, setBeach] = useState({loading:false, dt:null})
    let content = null

    useEffect(() => {
        setBeach({
            loading: true, 
            dt: null
        })

        axios.get(url)
            .then(response => {
                setBeach({
                    loading: false,
                    dt: response.data.sensors
                })
        })
            .catch((err) => {
                console.log(err)
            })
    }, [])

    if(beach.loading) {
        content = <Loader />
    }

    if(beach.dt) {
            return (
                <h1 className = "p-5"> 
                    Name: {beach.dt[name].meta.name} <br/>
                    Location: {beach.dt[name].meta.lat}
                    Water temp: {beach.dt[name].data.slice(-1)[0].temp_water} <br/>
                    Air temp: {beach.dt[name].data.slice(-1)[0].temp_air} <br/>
                    Last updated: {beach.dt[name].data.slice(-1)[0].time}
                </h1>
            ) 
    }

    return (
        <div> {content} </div>
    )
}

export default SinglePage
