import React, {useState, useEffect, Fragment} from 'react';
import axios from 'axios';
import Loader from '../Components/Loader'
import SinglePage from './SinglePage';
import { Link } from 'react-router-dom';

function Home() {
    const url = 'https://iot.fvh.fi/opendata/uiras/uiras2_v1.json'
    const [beaches, setBeaches] = useState({loading:false, dt:null})
    let content = null

    useEffect(() => {
        setBeaches({
            loading: true, 
            dt: null
        })

        axios.get(url)
            .then(response => {
                setBeaches({
                    loading: false,
                    dt: response.data.sensors,
                })
        })
            .catch((err) => {
                console.log(err)
            })
    }, [])

    if(beaches.loading) {
        content = <Loader />
    }
    //get temp: {beaches.dt[beach].data.slice(-1)[0].temp_water}
    if(beaches.dt) {
        content = Object.keys(beaches.dt).map((beach, key) => {
            return (
                <h1 className = "p-5" beach = {beaches.dt[beach]} key = {key}> 
                    <Link to = {`/${beaches.dt[beach].meta.name}`}>
                        {beaches.dt[beach].meta.name}
                    </Link>
                </h1>
            )
        })
    }

    return (
        <div> {content} </div>
    )
}

export default Home
