import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';

function Search() {
    const [showSearch, setShowSearch] = useState(false)

    let searchMenu 
    let menuMask

    if(showSearch) {
        searchMenu= <div className = "fixed left-0 right-0 bg-white text-center w-1/2 z-50">
                This is the search box
            </div>

        menuMask = <div className = "bg-black-t-50 fixed top-0 left-0 w-full h-full z-50" onClick={() => setShowSearch(false)}>
        </div>
    }

    return (
        <div>
            <span className="text-center font-bold p-3">
                <FontAwesomeIcon
                    icon = {faSearch}
                    onClick = {() => setShowSearch(!showSearch)}
                />
            </span>

            <div className = "opacity-0 fixed w-full h-full">

            </div>
        </div>
    );
}

export default Search
