import React, {useState, useEffect, Fragment} from 'react';
import axios from 'axios';
import Loader from '../Components/Loader'

function Home() {
    const url = 'https://iot.fvh.fi/opendata/uiras/uiras2_v1.json'
    const [beaches, setBeaches] = useState({loading:false, dt:null, error:false})
    let content = null

    useEffect(() => {
        setBeaches({
            loading: true, 
            dt: null,
            error:false
        })

        axios.get(url)
            .then(response => {
                setBeaches({
                    loading: false,
                    dt: response.data.sensors,
                    error: false
                })
        })
            .catch(() => {
                setBeaches({
                    loading: false,
                    dt: null,
                    error: true
                })
            })
    }, [])

    if(beaches.error) {
        content = <p>Page not found</p>
    }


    if(beaches.loading) {
        content = <Loader />
    }

    if(beaches.dt) {
        content = 
        Object.keys(beaches.dt).map((item, index) => {
            return (
                <h1 className = "p-5" value = {beaches.dt[item].code} key = {index}> 
                    {beaches.dt[item].meta.name} and temp water is {beaches.dt[item].data.slice(-1)[0].temp_water} Â°C
                </h1>
            )
        })
    }

    return (
        <div> {content} </div>
    )
}

export default Home
