import React, {useState, useEffect} from 'react';
import axios from 'axios';

function Home() {
    const url = 'https://iot.fvh.fi/opendata/uiras/uiras2_v1.json'
    const [beach, setBeach] = useState(null)

    useEffect(() => {
        axios.get(url)
            .then(response => {
                setBeach(response.data)
        })
    }, [url])

    if(beach) {
        return(
            <div>
                <h2> {beach.sensors} </h2>
            </div>
        )
    }

    return (
        <div>
            <h1 className = "font-bold text-xl text-left p-5">Beaches near you</h1> 


        </div>
    )
}

export default Home
