import React, {useState, useEffect, Fragment} from 'react';
import axios from 'axios';

function Home() {
    const url = 'https://iot.fvh.fi/opendata/uiras/uiras2_v1.json'
    const [beaches, setBeaches] = useState({ loading:false, dt:[]} )

    useEffect(() => {
        setBeaches({
            loading: true, 
            dt: []
        })

        axios.get(url)
            .then(response => {
                setBeaches({
                    loading: false,
                    dt: response.data.sensors
                })
        })
            .catch((err) => {
                console.log(err)
            })
    }, [])

    if(beaches) {
        return (
            Object.keys(beaches).map((item, index) => {
                return (
                    <h1 className = "p-5" value = {beaches[item].code} key = {index}> 
                        {beaches[item].dt.meta.name} and temp water is {beaches[item].dt.data.slice(-1)[0].temp_water} Â°C
                    </h1>
                )
            })
        )
    }

    return (
        <div></div>
    )
}

export default Home
