import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars } from '@fortawesome/free-solid-svg-icons';
import {useTransition, animated} from 'react-spring'

function Navigation() {
    const [showMenu, setShowMenu] = useState(false)
    //Transition effect
    const maskTransitions = useTransition(showMenu, null, {
        from: { position: 'absolute', opacity: 0 },
        enter: { opacity: 1 },
        leave: { opacity: 0 },
        })

    const transitions = useTransition(showMenu, null, {
        from: { opacity: 0, transform: 'translateX(-100%)' },
        enter: { opacity: 1, transform: 'translateX(0%)' },
        leave: { opacity: 0, transform: 'translateX(-100%)' },
        })


    return (
        <nav>
            <span className="text-center border-b font-bold p-3">
                <FontAwesomeIcon
                    icon = {faBars}
                    onClick = {() => setShowMenu(!showMenu)}
                />
            </span>

            {
                maskTransitions.map(({ item, key, props }) =>
                item && 
                <animated.div 
                    key={key} 
                    style={props}
                    className = "fixed bg-black-t-50 top-0 left-0 w-full h-full z-50"
                    onClick = {() => setShowMenu(false)}
                >
                </animated.div>
                )
            }            

            {
                transitions.map(({ item, key, props }) =>
                item && 
                <animated.div 
                    key={key} 
                    style={props}
                    className = "fixed bg-white top-0 left-0 w-2/3 h-full shadow z-50 shadow"
                >
                    <div>
                        <span className = "text-center font-bold width-full">City Swimmer</span>
                    </div>
                </animated.div>
                )
            }
        </nav>
    );
}

export default Navigation
