import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import AdvanceSearch from '../Views/AdvanceSearch';
import {useTransition, animated} from 'react-spring'

function Search() {
    const [showSearch, setShowSearch] = useState(false)
    let searchMenu, menuMask
    const centered = {
        position: "fixed",
        top: "50%",
        left: "50%",
        transform: "translate(-50%,-50%)"
    }
    const transitions = useTransition(showMenu, null, {
        from: { opacity: 0, transform: 'translateX(-100%)' },
        enter: { opacity: 1, transform: 'translateX(0%)' },
        leave: { opacity: 0, transform: 'translateX(-100%)' },
        })

    if(showSearch) {
        searchMenu = 
            <div style={centered} className = "items-center w-1/2 bg-white z-50">
                <AdvanceSearch />
            </div>

        menuMask = <div className = "bg-black-t-50 fixed top-0 left-0 w-full h-full z-50" onClick={() => setShowSearch(false)}></div>
    }

    return (
        <div>
            <span className="text-center font-bold p-3">
                <FontAwesomeIcon
                    icon = {faSearch}
                    onClick = {() => setShowSearch(!showSearch)}
                />
            </span>

            {menuMask}
            {searchMenu}
        </div>
    );
}

export default Search
