import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';

function Search() {
    const [showSearch, setShowSearch] = useState(false)
    let searchMenu, menuMask
    const centered = {
        position: "fixed",
        top: "50%",
        left: "50%",
        transform: "translate(-50%,-50%)"
    }

    if(showSearch) {
        searchMenu = 
            <div style={centered} className = "text-center w-1/2 bg-white z-50">
                <AdvanceSearch />
            </div>

        menuMask = <div className = "bg-black-t-50 fixed top-0 left-0 w-full h-full z-50" onClick={() => setShowSearch(false)}></div>
    }

    return (
        <div>
            <span className="text-center font-bold p-3">
                <FontAwesomeIcon
                    icon = {faSearch}
                    onClick = {() => setShowSearch(!showSearch)}
                />
            </span>

            {menuMask}
            {searchMenu}
        </div>
    );
}

export default Search
